    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (91, 'ABNT NBR 16693:2018', 'Produtos têxteis para saúde – Aventais e roupas privativas para procedimento não cirurgico utilizados por profissionais de saúde e pacientes – Requisitos e métodos de ensaio', 'Esta Norma especifica os requisitos e os métodos de ensaio para avaliação das características de aventais e roupas privativas para procedimentos não cirugicos, de uso único ou reutilizáveis,utilizados como produtos para saúde por pacientes e profissionais de saúde.', '59', '["Produto para saúde","Material têxtil","Hospital","Roupa hospitalar"]', 'ABNT NBR 16693_2018.pdf');

