    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (111, 'ABNT NBR IEC 62366:2016', 'Produtos para a saúde — Aplicação da engenharia de usabilidade a produtos para a saúde', 'Esta Norma especifica um processo para o fabricante analisar, especificar, desenvolver, verificar e validar a usabilidade, relacionadas à segurança de produtos para a saúde. Este processo de engenharia de usabilidade avalia e mitiga riscos causados por problemas de usabilidade associados à utilização correta e ao erro de utilização, sob utilização normal. Pode ser utilizado para identificar, mas não para avaliar ou mitigar riscos associados à utilização anormal.', '11', '["Produto para saúde","Segurança","Gerenciamento de risco"]', 'ABNT NBR IEC 62366_2016.pdf');

    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (121, 'ABNT ISO/TR 24971:2014', 'Produtos para a saúde – Orientações para a aplicação da ABNT NBR ISO 14971', 'Este Relatório Técnico prove orientação no tratamento de partes específicas da ABNT NBR ISO 14971 na implantação do gerenciamento de risco.', '11', '["Equipamento médico"]', 'ABNT ISO_TR 24971_2014.pdf');

    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (131, 'ABNT NBR ISO 14971:2020 Versão Corrigida:2020', 'Dispositivos médicos — Aplicação de gerenciamento de risco a dispositivos médicos', 'Este documento especifica a terminologia, os princípios e um processo para gerenciamento de risco de produtos para saúde, incluindo “software” como produto para saúde e produtos para saúde de diagnóstico in vitro. O processo descrito neste documento é destinado a auxiliar os fabricantes de produtos para saúde a identificar os perigos associados com o produto para saúde, estimar e avaliar os riscos associados, controlar esses riscos e monitorar a eficácia dos controles.', '11', '["Produto para saúde"]', 'ABNT NBR ISO 14971_2020 Versão Corrigida_2020.pdf');

    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (141, 'ABNT NBR IEC 60601-1-10:2010 Versão Corrigida:2013 & Emenda 1:2017', 'Equipamento eletromédico', 'Esta Norma aplica-se à SEGURANÇA BÁSICA e DESEMPENHO ESSENCIAL dos EQUIPAMENTOS ELETROMÉDICOS e dos SISTEMAS ELETROMÉDICOS, a partir daqui referenciados como EQUIPAMENTOS EM e SISTEMAS EM.', '11', '["Equipamento médico","Equipamento eletromédico"]', 'ABNT NBR IEC 60601-1-10_2010 Versão Corrigida_2013 & Emenda 1_2017.pdf');

