    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (171, 'ABNT NBR IEC 60601-1-6:2011 Versão Corrigida:2013', 'Equipamento eletromédico', 'Esta norma especifica um PROCESSO para que o FABRICANTE analise, especifique, projete, VERIFIQUE e VALIDE a USABILIDADE, quando relacionada à SEGURANÇA BÁSICA e ao DESEMPENHO ESSENCIAL dos EQUIPAMENTOS ELETROMÉDICOS, daqui por diante denominados EQUIPAMENTOS EM.', '11', '["Requisito","Segurança","Equipamento médico"]', 'ABNT NBR IEC 60601-1-6_2011 Versão Corrigida_2013.pdf');

