    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (191, 'ABNT NBR ISO 18562-1:2020', 'Avaliação de biocompatibilidade de vias de gás de respiração em aplicações de cuidados à saúde', 'Este documento especifica: os princípios gerais que governam a avaliação biológica dentro de um processo de gerenciamento de risco vias de gás produtos para a saúde, suas partes ou acessórios, que são destinadas a fornecer cuidado respiratório ou fornecer substâncias por meio do trato respiratório ao paciente em todos os ambientes; a categorização geral de vias de gás com base na natureza e na duração do seu contato com o fluxo de gás; a avaliação de dados existentes pertinentes de todas as fontes; a identificação de lacunas no conjunto de dados disponíveis sobre a base de uma análise de risco; a identificação de conjuntos de dados adicionais necessária para analisar a segurança biológica via de gás; a avaliação da segurança biológica da via de gás.', '11', '["Avaliação de risco","Anestesia","Equipamento de respiração"]', 'ABNT NBR ISO 18562-1_2020.pdf');

    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (201, 'ABNT NBR ISO 18562-2:2020', 'Avaliação de biocompatibilidade de vias de gás de respiração em aplicações de cuidados à saúde', 'Este documento especifica os ensaios para as emissões de matéria particulada vias de gás produto para a saúde, suas partes ou acessórios, os quais são destinados a fornecer cuidados respiratórios ou a fornecer substâncias pelo trato respiratório para um paciente em todos os ambientes.', '11', '["Avaliação de risco","Anestesia","Equipamento de respiração"]', 'ABNT NBR ISO 18562-2_2020.pdf');

    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (211, 'ABNT NBR ISO 10651-6:2015', 'Ventiladores pulmonares para uso médico — Requisitos particulares para segurança e desempenho essencial (Parte 6: Equipamento de suporte ventilatório para uso domiciliar)', 'Esta Parte da ABNT NBR ISO 10651 especifica os requisitos de segurança básica e desempenho essencial para dispositivos de suporte ventilatório domiciliar, destinados principalmente para uso domiciliar, mas que poderiam ser usados em outro lugar (por exemplo, em instituições de saúde) apropriados para pacientes para os quais não é necessário o uso de ventilador de uso domiciliar em conformidade com ISO 10651-2.', '11', '["Equipamento médico","Ventilador pulmonar","Sistema de ventilação","Ventilador de uso domiciliar","Equipamento de uso domiciliar"]', 'ABNT NBR ISO 10651-6_2015.pdf');

    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (221, 'ABNT NBR ISO 10651-4:2011', 'Ventiladores pulmonares (Parte 4: Requisitos particulares para reanimadores operados manualmente)', 'Esta Parte da ABNT NBR ISO 10651 especifica os requisitos de segurança básica e desempenho essencial para dispositivos de suporte ventilatório domiciliar, destinados principalmente para uso domiciliar, mas que poderiam ser usados em outro lugar (por exemplo, em instituições de saúde) apropriados para pacientes para os quais não é necessário o uso de ventilador de uso domiciliar em conformidade com ISO 10651-2.', '11', '["Equipamento médico","Ventilador pulmonar","Sistema de ventilação","Ventilador de uso domiciliar","Equipamento de uso domiciliar"]', 'ABNT NBR ISO 10651-4_2011.pdf');

    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (231, 'ABNT NBR ISO 10651-3:2014', 'Ventiladores pulmonares para uso médico — Requisitos particulares de segurança e de desempenho essencial (Parte 3: Requisitos particulares para ventiladores de transporte e emergência)', 'Esta parte da ABNT NBR ISO 10651 é parte de uma série de normas com base na IEC 601-1:1988 (a “Norma Geral”); este tipo de norma é referido como uma “Norma Particular”. Como indicado em 1.3 da IEC 601-1:1988, os requisitos desta parte da ABNT NBR ISO 10651 prevalecem sobre os da IEC 601-1:1988. Onde esta parte da ABNT NBR ISO 10651 especifica que a seção da IEC 601-1 é aplicável, significa que a seção só se aplica se o requisito for relevante para o ventilador em consideração.', '11', '["Ventilador pulmonar","Sistema de ventilação"]', 'ABNT NBR ISO 10651-3_2014.pdf');

    INSERT INTO normas (id, codigo, titulo, objetivo, ics, palavrasChave, filename)
    VALUES (241, 'ABNT NBR ISO 10651-5:2017', 'Ventiladores pulmonares para uso médico — Requisitos particulares de segurança e de desempenho essencial (Parte 5: Reanimadores de emergência a gás)', 'Esta Parte da ABNT NBR ISO 10651 especifica os requisitos básicos de segurança e desempenho para reanimadores de emergência a gás (3.10), para uso com humanos por socorristas. Este equipamento se destina à área de emergências e é destinado a ser continuamente assistido por um operador em uso normal.', '11', '["Equipamento médico","Ventilador pulmonar","Sistema de ventilação","Ventilador de uso domiciliar"]', 'ABNT NBR ISO 10651-5_2017.pdf');
